mapper.select:
select
*
from t_equipment_bbu
where network_type=#{equipmentBBU.networkType}

	and bbu_name like '%${equipmentBBU.bbuName}%'

limit #{startIndex},#{endIndex}

SELECT dx_code as dxCode, count(0) as oltCount, sum(power) as power FROM t_equipment_olt WHERE dx_code IN
#{item}
GROUP BY dx_code

select  * from t_equipment_rru_aau


		and network_type=#{equipmentRRUAAU.networkType}


		and rru_name like '${%equipmentRRUAAU.rruName%}'


limit #{startIndex},#{endIndex}

select * from t_equipment_bf
and bf_name like '%${equipmentBoFen.bfName}%'
limit #{startIndex},#{endIndex}

insert.foreach:
             insert into t_equipment_rru_aau (
                   dx_code,rru_code,rru_name,power,
                   network_type
              )
            values (#{item.dxCode},#{item.rruCode},#{item.rruName},#{item.power},
                    #{item.networkType}
              )

				dx_code=#{item.dxCode},
				rru_name=#{item.rruName},
				power=#{item.power},
				network_type=#{item.networkType}

             insert into t_equipment_olt (
                   dx_code,olt_code,olt_name,power)
            values (#{item.dxCode},#{item.oltCode},#{item.oltName},#{item.power}
              )

			dx_code=#{item.dxCode},
			olt_name=#{item.oltName},
			power=#{item.power},
			olt_code=#{item.oltCode}

             insert into t_equipment_bf (
                   dx_code,bf_code,bf_name,power)
            values (#{item.dxCode},#{item.bfCode},#{item.bfName},#{item.power}
              )

			dx_code=#{item.dxCode},
			bf_name=#{item.bfName},
			power=#{item.power},
			bf_code=#{item.bfCode}

mapper.select:
select * from t_base_station
and base_name like '%${sitManager.baseName}%'
limit #{startIndex},#{endIndex}

select * from t_equipment_olt
and olt_name like '%${equipmentOLT.oltName}%'
limit #{startIndex},#{endIndex}

select * from t_equipment_ipran
and ipran_name like '%${equipmentIPRAN.ipranName}%'
limit #{startIndex},#{endIndex}

insert.foreach:
insert into t_equipment_ipran (
dx_code,ipran_code,ipran_name,power)
values (#{item.dxCode},#{item.ipranCode},#{item.ipranName},#{item.power}
)

	dx_code=#{item.dxCode},
	ipran_name=#{item.ipranName},
	power=#{item.power}

mapper.update:
UPDATE t_base_station t SET 	t.three_bbu_power=0 WHERE  t.three_bbu_power is null ;
UPDATE t_base_station t SET 	t.four_bbu_power=0 WHERE  t.four_bbu_power is null ;
UPDATE t_base_station t SET 	t.five_bbu_power=0 WHERE  t.five_bbu_power is null ;
UPDATE t_base_station t SET 	t.three_rru_power=0 WHERE  t.three_rru_power is null ;
UPDATE t_base_station t SET 	t.four_rru_power=0 WHERE  t.four_rru_power is null ;
UPDATE t_base_station t SET 	t.five_aau_power=0 WHERE  t.five_aau_power is null ;
UPDATE t_base_station t SET 	t.olt_power=0 WHERE  t.olt_power is null ;
UPDATE t_base_station t SET 	t.ipran_power=0 WHERE  t.ipran_power is null ;
UPDATE t_base_station t SET 	t.bf_power=0 WHERE  t.bf_power is null ;
UPDATE t_base_station t SET 	t.power_Consume=(t.three_bbu_power+t.four_bbu_power+t.five_bbu_power+t.three_rru_power+t.four_rru_power+t.five_aau_power+t.olt_power+t.ipran_power+t.bf_power);
